import cv2# Initialize video capture (0 for the default camera)cameraCapture = cv2.VideoCapture(0)# Define the frames per secondfps = 30# Get the frame sizesize = (int(cameraCapture.get(cv2.CAP_PROP_FRAME_WIDTH)),        int(cameraCapture.get(cv2.CAP_PROP_FRAME_HEIGHT)))# Initialize the video writer with the correct function name and argumentsvideoWriter = cv2.VideoWriter(    'output.avi',    cv2.VideoWriter_fourcc('I', '4', '2', '0'),    fps,    size)# Read the first framesuccess, frame = cameraCapture.read()# Define the number of frames to capture (10 seconds of video)numOfFramesRemaining = 10 * fps - 1  # 10 seconds of frames# Loop to capture and write frames to the video filewhile success and numOfFramesRemaining > 0:    videoWriter.write(frame)    success, frame = cameraCapture.read()    numOfFramesRemaining -= 1